@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 250 20% 6%;
    --foreground: 250 10% 95%;

    --card: 250 20% 10%;
    --card-foreground: 250 10% 95%;

    --popover: 250 20% 8%;
    --popover-foreground: 250 10% 95%;

    --primary: 280 100% 70%;
    --primary-foreground: 250 20% 6%;

    --secondary: 320 80% 60%;
    --secondary-foreground: 250 10% 95%;

    --muted: 250 15% 18%;
    --muted-foreground: 250 10% 60%;

    --accent: 280 60% 50%;
    --accent-foreground: 250 10% 95%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --border: 250 15% 20%;
    --input: 250 15% 18%;
    --ring: 280 100% 70%;

    --radius: 1rem;

    /* Custom tokens */
    --gradient-primary: linear-gradient(135deg, hsl(280 100% 70%) 0%, hsl(320 80% 60%) 50%, hsl(340 90% 65%) 100%);
    --gradient-secondary: linear-gradient(135deg, hsl(250 60% 50%) 0%, hsl(280 100% 70%) 100%);
    --gradient-glow: radial-gradient(ellipse at center, hsl(280 100% 70% / 0.3) 0%, transparent 70%);
    --gradient-card: linear-gradient(145deg, hsl(250 20% 12%) 0%, hsl(250 20% 8%) 100%);

    --shadow-glow: 0 0 60px hsl(280 100% 70% / 0.3);
    --shadow-card: 0 8px 32px hsl(250 50% 5% / 0.5);
    --shadow-button: 0 4px 20px hsl(280 100% 70% / 0.4);

    --glass-bg: hsl(250 20% 10% / 0.6);
    --glass-border: hsl(250 15% 30% / 0.3);
  }
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer components {
  .glass-card {
    background: var(--glass-bg);
    backdrop-filter: blur(20px);
    border: 1px solid var(--glass-border);
  }

  .gradient-text {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .gradient-border {
    position: relative;
    background: var(--gradient-card);
    border-radius: var(--radius);
  }

  .gradient-border::before {
    content: "";
    position: absolute;
    inset: 0;
    padding: 1px;
    border-radius: inherit;
    background: var(--gradient-primary);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    pointer-events: none;
  }

  .glow-effect {
    box-shadow: var(--shadow-glow);
  }

  .upload-zone {
    background: linear-gradient(145deg, hsl(250 20% 12%) 0%, hsl(250 20% 9%) 100%);
    border: 2px dashed hsl(250 15% 25%);
    transition: all 0.3s ease;
  }

  .upload-zone:hover {
    border-color: hsl(280 100% 70%);
    box-shadow: inset 0 0 30px hsl(280 100% 70% / 0.1);
  }

  .clothing-card {
    background: var(--gradient-card);
    border: 1px solid hsl(250 15% 20%);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .clothing-card:hover {
    border-color: hsl(280 100% 70% / 0.5);
    transform: translateY(-4px);
    box-shadow: 0 12px 40px hsl(280 100% 70% / 0.2);
  }

  .clothing-card.selected {
    border-color: hsl(280 100% 70%);
    box-shadow: 0 0 20px hsl(280 100% 70% / 0.4);
  }
}

@layer utilities {
  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-pulse-glow {
    animation: pulseGlow 3s ease-in-out infinite;
  }

  .animate-shimmer {
    animation: shimmer 2s linear infinite;
    background-size: 200% 100%;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes pulseGlow {
  0%, 100% {
    opacity: 0.6;
    transform: scale(1);
  }
  50% {
    opacity: 1;
    transform: scale(1.05);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

